pragma solidity 0.8.12;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// Rove is the utility token of the Rove system.
//
// * Total supply: 1 billion tokens
//
// * Minting schedule: 
//     - 40% 1st year
//     - 30% 2nd year
//     - 20% 3rd year
//     - 10% 4th year
